package question.day0713;

public class bangJjo_0713 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i = 2; i <= 4; i++) {
			for(int j = 1; j <= 5; j++) {
				System.out.printf("%d * %d =%3d",i,j,i*j);
				System.out.print("   ");
			} // end for
			System.out.println();
		} // end for
		
	} // main

} // class
